function trialOutput = AdaptationExperiment(subjectName,exptdesign)try    % following codes should be used when you are getting key presses using    % fast routines like kbcheck.    KbName('UnifyKeyNames');    deleteKeyNum=KbName('delete');    escapeKeyNum=KbName('escape');    spaceKeyNum=KbName('space');    Priority(1)        enterKeyNum=77; % for some unknown reason, KbName('enter') returns 53, which does not work on this setup...    endKeyNum=KbName('end');    escapeCharNum=27;        %settings so that Psychtoolbox doesn't display annoying warnings--DON'T CHANGE    oldLevel = Screen('Preference', 'VisualDebugLevel', 1);        %     oldEnableFlag = Screen('Preference', 'SuppressAllWarnings', 1);    %     warning off    HideCursor;        WaitSecs(1); % make sure it is loaded into memory;    tmp=GetSecs; % make sure it is loaded into memory;        % initialize the random number generator    randn('state',sum(100*clock));                %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%    %		INITIALIZE EXPERIMENT    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%    % open a screen and display instructions    % Choosing the display with the highest display number is    % a best guess about where you want the stimulus displayed.    screens=Screen('Screens');    screenNumber=0;    %screenNumber=max(screens);        % Open window with default settings:    [w windowRect] =Screen('OpenWindow', screenNumber,[128 128 128]);    white = WhiteIndex(w); % pixel value for white    gray = GrayIndex(w); % pixel value for gray    black = BlackIndex(w); % pixel value for black    font='-misc-fixed-bold-r-normal--13-100-100-100-c-70-iso8859-1';    [o o1]=Screen(w, 'TextFont', font)        %  calculate the center of the screen, for later reference    center = [(windowRect(3)-windowRect(1))/2 (windowRect(4)-windowRect(2))/2];    %  calculate the slack allowed during a flip interval    refresh = Screen('GetFlipInterval',w);    slack = refresh/2;    refresh    slack            % Select specific text font, style and size, unless we're on Linux    % where this combo is not available:    if IsLinux==0        Screen('TextFont',w, 'Courier New');        Screen('TextSize',w, 14);        Screen('TextStyle', w, 1+2);    end;        if exptdesign.netstationPresent        % Connect to Netstation        [status error] = NetStation('Connect', exptdesign.netstationIP)        if status ==1 % there was an error!            ME = MException('NETSTATION:CouldNotConnect', ['Could not connect to Netstation computer at IP ' exptdesign.netstationIP '.  Please check the IP and network connection and try again.\n  Error:' error]);            throw(ME);        end                %         % Tell Netstation to start recording        %             [status error] = NetStation('StartRecording')        %         if status ==1 % there was an error!        %              ME = MException('NETSTATION:CouldNotRecord', ['Could not tell Netstation to start recording.  Please check the IP and connection and try again.\n  Error:' error]);        %              throw(ME);        %         end                % Tell Netstation to synchronize recording        [status error] = NetStation('Synchronize',exptdesign.netstationSyncLimit);        if status ==1 % there was an error!            ME = MException('NETSTATION:CouldNotSync', ['Could not sync with Netstation to allowable limit of ' exptdesign.syncLimit '.  Please check the IP and connection and try again.\n  Error:' error]);            throw(ME);        end    end        %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%    %		INTRO EXPERIMENT    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%        % Now horizontally and vertically centered:    drawAndCenterText(w,'Press any key to start experiment...');        % cache the files that are needed    line1files1 = dir(exptdesign.line1files1);   