/ZUPUD        %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%        if exptdesign.responseType==1            drawAndCenterText(w,'INSTRUCTIONS: \n \n View each pair of images. \n Push the left mouse button when images are from the same category. \n Push the right mouse button when images are from different categories.  \n Push any key when you are ready to begin. ');        else            drawAndCenterText(w,'INSTRUCTIONS: \n \n View each pair of images. \n Push the right mouse button when images are from the same category. \n Push the left mouse button when images are from different categories.  \n Push any key when you are ready to begin. ');        end                        % Tell Netstation to start recording        if exptdesign.netstationPresent            [status error] = NetStation('StartRecording');            if status ==1 % there was an error!                status                error                ME = MException('NETSTATION:CouldNotRecord', ['Could not tell Netstation to start recording.  Please check the IP and connection and try again.\n  Error:' error]);                throw(ME);            end            %  Wait for Netstation to Start Recording            WaitSecs(2);        end                drawAndCenterText(w,'Please hold still for 5 seconds...',0);        pause(5)        drawAndCenterText(w,'Press a key to begin',1);                %         if exptdesign.randomizeMaskPresentation        % %             useMask = ceil(2* rand(1)) - 1;        %             useMask = mod(sessionNum-1,2);        %         end                %  Go through all trials for this session        for trial=1:numTrials            jitter=randperm(6,1);            %  Choose the SOA interval for this trial            %numSOAs = size(exptdesign.soaDurations,2);            isiDuration = exptdesign.isiDuration;            %soaDuration = exptdesign.soaDurations(ceil(numSOAs*rand(1)));                        %  Make all of the textures and data needed for this trial            %mytext =['Please rate the visibility of the picture just presented.\n \n 1.  I did not see the picture. \n 2. I do not think I saw the picture. \n 3. I think I saw something. \n 4. I saw the picture.'];            fixationTexture=Screen('MakeTexture', w, double(fixationimage));            blankTexture=Screen('MakeTexture', w, double(blankimage));                                    stimulus1Texture=Screen('MakeTexture', w, squeeze(allimages(lineThisBlock(trial),imagesThisBlock(1,trial),:,:,jitter)));                        stimulus2Texture=Screen('MakeTexture', w, squeeze(allimages(lineThisBlock(trial),imagesThisBlock(2,trial),:,:,jitter)));                                    % %             if (useMask)            % %                 maskTexture=Screen('MakeTexture', w, squeeze(maskimages(trial,:,:)));            % %             else            % %                 maskTexture=Screen('MakeTexture', w, double(blankimage));            % %             end                                    %  Set up the trial output data            %trialOutput(sessionNum).trials(trial).category = category;            trialOutput(sessionNum).trials(trial).sessionIndex = sessionNum;            trialOutput(sessionNum).trials(trial).trialIndex = trial;            trialOutput(sessionNum).trials(trial).stimulus1typemarker = stimulus1typemarkerThisBlock(trial);            trialOutput(sessionNum).trials(trial).stimulus2typemarker = stimulus2typemarkerThisBlock(trial);            trialOutput(sessionNum).trials(trial).imagefile1 = allimagefiles(lineThisBlock(trial),imagesThisBlock(1,trial),jitter);            trialOutput(sessionNum).trials(trial).imagefile2 = allimagefiles(lineThisBlock(trial),imagesThisBlock(2,trial),jitter);            trialOutput(sessionNum).trials(trial).jitter=jitter;                                    %trialOutput(sessionNum).trials(trial).imagefile2 = allimagefiles(lineThisBlock(trial),imagesThisBlock(2,trial),1);            trialOutput(sessionNum).trials(trial).condition = conditionThisBlock(trial);            trialOutput(sessionNum).trials(trial).line = lineThisBlock(trial);            trialOutput(sessionNum).trials(trial).imagePosition1 = imagesThisBlock(1,trial);            trialOutput(sessionNum).trials(trial).imagePosition2 = imagesThisBlock(2,trial);            trialOutput(sessionNum).trials(trial).trialStartTime=GetSecs;                                    if exptdesign.responseType==1                if stimulus1typemarkerThisBlock(trial) < 3                    trialOutput(sessionNum).trials(trial).correctResponse=1;                elseif stimulus1typemarkerThisBlock(trial) ==5                    trialOutput(sessionNum).trials(trial).correctResponse=0;                else                    trialOutput(sessionNum).trials(trial).correctResponse=2;                end            else                if stimulus1typemarkerThisBlock(trial) < 3                    trialOutput(sessionNum).trials(trial).correctResponse=2;                elseif stimulus1typemarkerThisBlock(trial) ==5                    trialOutput(sessionNum).trials(trial).correctResponse=0;                else                    trialOutput(sessionNum).trials(trial).correctResponse=1;                end            end                        % intertrial variation 1000-1500 ms            interTrialDuration = (ceil(500* rand(1)) + 200) / 1000;            %            interTrialDuration = (ceil(500* rand(1)) + 500) / 1000;                        %  Fixation display            Screen('DrawTexture', w, fixationTexture);            Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [FixationVBLTimestamp FixationOnsetTime FixationFlipTimestamp FixationMissed] = Screen('Flip',w);            %             WaitSecs(exptdesign.fixationDuration);                        Screen('DrawTexture', w, blankTexture);            Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [InterTrialBlankVBLTimestamp InterTrialBlankOnsetTime InterTrialBlankFlipTimestamp InterTrialBlankMissed] = Screen('Flip',w,FixationVBLTimestamp + exptdesign.fixationDuration - slack);                                    %  Stimulus display            Screen('DrawTexture', w, stimulus1Texture);            Screen('FillRect',w,white,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [Stimulus1VBLTimestamp Stimulus1OnsetTime Stimulus1FlipTimestamp Stimulus1Missed] = Screen('Flip',w,InterTrialBlankVBLTimestamp+interTrialDuration - slack);                                    %             [StimulusVBLTimestamp StimulusOnsetTime StimulusFlipTimestamp StimulusMissed] = Screen(w, 'Flip',FixationOnsetTime+exptdesign.fixationDuration - slack);            %             WaitSecs(exptdesign.stimulusDuration);            %             secstart = GetSecs                        %             GetSecs - StimulusOnsetTime            %  Blank display for the SOA interval            Screen('DrawTexture', w, blankTexture);            Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [Blank1VBLTimestamp Blank1OnsetTime Blank1FlipTimestamp Blank1Missed] = Screen('Flip',w,Stimulus1VBLTimestamp+exptdesign.stimulusDuration-slack);            %             [BlankVBLTimestamp BlankOnsetTime BlankFlipTimestamp BlankMissed] = Screen(w, 'Flip',StimulusOnsetTime+exptdesign.stimulusDuration-slack);            %             secend = GetSecs -secstart            %             WaitSecs(exptdesign.soaDuration);                        Screen('DrawTexture', w, stimulus2Texture);            Screen('FillRect',w,white,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [Stimulus2VBLTimestamp Stimulus2OnsetTime Stimulus2FlipTimestamp Stimulus2Missed] = Screen('Flip',w,Blank1VBLTimestamp+isiDuration - slack);            AnimalResponseStartTime = GetSecs;            %             if exptdesign.netstationPresent            %                 Netstation('Event');            %             end                        Screen('DrawTexture', w, blankTexture);            Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            [Blank2VBLTimestamp Blank2OnsetTime Blank2FlipTimestamp Blank2Missed] = Screen('Flip',w,Stimulus2VBLTimestamp+exptdesign.stimulusDuration-slack);            %            %             %  Mask display            %             Screen('DrawTexture', w, maskTexture);            %             Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            % %             Screen('FillRect',w,white,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            %             [MaskVBLTimestamp MaskOnsetTime MaskFlipTimestamp            %             MaskMissed] = Screen('Flip',w,BlankVBLTimestamp+soaDuration - slack);            % %             [MaskVBLTimestamp MaskOnsetTime MaskFlipTimestamp  MaskMissed] = Screen(w, 'Flip',BlankOnsetTime+exptdesign.soaDuration - slack);            % %             WaitSecs(exptdesign.maskDuration);                        %  Blank the screen until the next trial            %             Screen('DrawTexture', w, blankTexture);            %             Screen('FillRect',w,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiode            %             [BlankResponseVBLTimestamp BlankResponseOnsetTime BlankResponseFlipTimestamp BlankResponseMissed] = Screen( 'Flip',w,MaskVBLTimestamp+exptdesign.maskDuration-slack);            % %           [AnimalResponseVBLTimestamp AnimalResponseOnsetTime AnimalResponseFlipTimestamp AnimalResponseMissed] = Screen(w, 'Flip',MaskOnsetTime+exptdesign.maskDuration-slack);                                                if exptdesign.waitForResponse                numericalanswer = getAnimalResponseMouseWait();            else                numericalanswer = getAnimalResponseMouse(exptdesign.responseDuration);            end            AnimalResponseFinishedTime = GetSecs;            %             disp(['Translated animal response keyboard input to ' num2str(numericalanswer)]);            trialOutput(sessionNum).trials(trial).subjectAnimalResponse = numericalanswer;                                                            %  View Response Rating display            if exptdesign.getRatingResponse                DrawFormattedText(w, mytext, center(1,1) - 200,'center');                [RatingResponseVBLTimestamp RatingResponseOnsetTime RatingResponseFlipTimestamp RatingResponseMissed] = Screen(w, 'Flip');                if exptdesign.waitForResponse                    numericalanswer = getRatingResponseWait();                else                    numericalanswer = getRatingResponse(exptdesign.responseDuration);                end                RatingResponseFinishedTime = GetSecs;                                disp(['Translated rating response keyboard input to ' num2str(numericalanswer)]);                trialOutput(sessionNum).trials(trial).subjectRating = numericalanswer;            end                        %  Record the end time for the trial            trialOutput(sessionNum).trials(trial).trialEndTime= GetSecs;                                    %  Send trial specific events to Netstation for recording (if            %  present)            %  STIM = Stimulus            %  DIST = Distractor            if exptdesign.netstationPresent                trialDuration = trialOutput(sessionNum).trials(trial).trialStartTime - trialOutput(sessionNum).trials(trial).trialEndTime;                if  trialOutput(sessionNum).trials(trial).correctResponse == 1                    %                     Netstation('Event','STIM',trialOutput(sessionNum).trials(trial).trialStartTime,0.001,'sess',num2str(sessionNum), 'tria',num2str(trial),'soad',num2str(soaDuration));                    NetStation('Event','SAME',GetSecs,0.001,'sess',sessionNum, 'tria',trial,'cond',conditionThisBlock(trial), 'resp', numericalanswer, 'line', lineThisBlock(trial));                else                    NetStation('Event','DIFF',GetSecs,0.001,'sess',sessionNum, 'tria',trial,'cond',conditionThisBlock(trial), 'resp', numericalanswer, 'line', lineThisBlock(trial));                end            end                        %  Record whether the subject got the right answer            trialOutput(sessionNum).trials(trial).subjectHasRightAnswer = trialOutput(sessionNum).trials(trial).correctResponse == trialOutput(sessionNum).trials(trial).subjectAnimalResponse;            ACCthisblock(trial)=trialOutput(sessionNum).trials(trial).correctResponse == trialOutput(sessionNum).trials(trial).subjectAnimalResponse;            %  Record the filenames used for stimulus and masks            %%%%%%%%trialOutput(sessionNum).trials(trial).stimulus1ImageFile = allimagefiles1(trial).name;            %%%%%%%%trialOutput(sessionNum).trials(trial).stimulus2ImageFile = allimagefiles2(trial).name;            %            trialOutput(sessionNum).trials(trial).maskImageFile = maskimagefiles(trial).name;            %            trialOutput(sessionNum).trials(trial).maskUsed = useMask;                                    %  Record the SOA interval for this time            trialOutput(sessionNum).trials(trial).isiDuration = isiDuration;                        % Calculate and record trial timing            trialOutput(sessionNum).trials(trial).fixationOnset = FixationOnsetTime;            trialOutput(sessionNum).trials(trial).fixationDuration = InterTrialBlankVBLTimestamp - FixationOnsetTime;  % record the actual time on screen (just to be accurate)                        trialOutput(sessionNum).trials(trial).stimulus1Onset = Stimulus1OnsetTime;            trialOutput(sessionNum).trials(trial).stimulus1Duration = Blank1VBLTimestamp - Stimulus1OnsetTime;  % record the actual time on screen (just to be accurate)                        trialOutput(sessionNum).trials(trial).blank1Onset = Blank1OnsetTime;                        trialOutput(sessionNum).trials(trial).stimulus2Onset = Stimulus2OnsetTime;            trialOutput(sessionNum).trials(trial).stimulus2Duration = Blank2VBLTimestamp - Stimulus2OnsetTime;  % record the actual time on screen (just to be accurate)                        trialOutput(sessionNum).trials(trial).blank2Onset = Blank2OnsetTime;            %            trialOutput(sessionNum).trials(trial).blankDuration = MaskVBLTimestamp - BlankOnsetTime;  % record the actual time the blank was on the screen (just to be accurate)                        %            trialOutput(sessionNum).trials(trial).maskOnset = MaskOnsetTime;            %            trialOutput(sessionNum).trials(trial).maskDuration = BlankResponseVBLTimestamp - MaskOnsetTime;  % record the actual time the blank was on the screen (just to be accurate)                        trialOutput(sessionNum).trials(trial).animalResponseStart = AnimalResponseStartTime;            trialOutput(sessionNum).trials(trial).animalResponseFinished = AnimalResponseFinishedTime;  % record the actual time the blank was on the screen (just to be accurate)                        if exptdesign.getRatingResponse                trialOutput(sessionNum).trials(trial).ratingResponseOnset = RatingResponseOnsetTime;                trialOutput(sessionNum).trials(trial).ratingResponseDuration = RatingResponseFinishedTime - RatingResponseOnsetTime;  % record the actual time the blank was on the screen (just to be accurate)            else                trialOutput(sessionNum).trials(trial).ratingResponseOnset = -1;                trialOutput(sessionNum).trials(trial).ratingResponseDuration = -1;  % record the actual time the blank was on the screen (just to be accurate)            end            trialOutput(sessionNum).trials(trial).fixationMissed = FixationMissed;            trialOutput(sessionNum).trials(trial).stimulus1Missed = Stimulus1Missed;            trialOutput(sessionNum).trials(trial).blank1Missed = Blank1Missed;            trialOutput(sessionNum).trials(trial).stimulus2Missed = Stimulus2Missed;            trialOutput(sessionNum).trials(trial).blank2Missed = Blank2Missed;            %            trialOutput(sessionNum).trials(trial).maskMissed = MaskMissed;            trialOutput(sessionNum).trials(trial).badTrial = ( FixationMissed > 0) |( Stimulus1Missed > 0) | ( Blank1Missed > 0) | ( Blank2Missed > 0) | (Stimulus2Missed);                        trialOutput(sessionNum).trials(trial).interTrialDuration = interTrialDuration;                        %  Close all open textures            Screen('Close', fixationTexture);            Screen('Close', stimulus1Texture);            Screen('Close', stimulus2Texture);            Screen('Close', blankTexture);            %    Screen('Close', maskTexture);                        if mod(trial, 49)==0                drawAndCenterText(w, 'Please take a quick blink break, press any key to continue.',1);            end                                end                drawAndCenterText(w,'Please hold still for 5 seconds...',0);        pause(5)                if exptdesign.netstationPresent            %  Tell Netstation to Stop Recording            [status error] = NetStation('StopRecording');            if status ==1 % there was an error!                status                error                ME = MException('NETSTATION:CouldNotStopRecording', ['Could not tell Netstation to stop recording.  Please check the IP and connection and try again.\n  Error:' error]);                throw(ME);            end        end        %  Write the trial specific data to the output file.        tic;        save([subjectName '.' num2str(sessionNum)'.' num2str(trial) '.mat'],'trialOutput','exptdesign');        toc;        %  Display a short break message        acc=sum(ACCthisblock)./length(ACCthisblock);        if ( sessionNum < exptdesign.numSessions)            drawAndCenterText(w,['End of Block #' num2str(sessionNum) '.\n\n You got ' num2str(acc.*100) '% correct. \n\n Please take a short break.\n  Then press any key to continue.']);        else            drawAndCenterText(w,['End of Block #' num2str(sessionNum) '.\n\n Thank you for participating!\n  Press any key to end the experiment.']);        end                    end            %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%    %		END    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%            % End of demo, close window:    Screen('CloseAll');    Priority(0);    % At the end of your code, it is a good idea to restore the old level.    %     Screen('Preference','SuppressAllWarnings',oldEnableFlag);        catch    % This "catch" section executes in case of an error in the "try"    % section []    if exptdesign.netstationPresent        % Tell Netstation to stop recording        [status error] = NetStation('StopRecording');        if status ==1 % there was an error!            status            error            ME = MException('NETSTATION:CouldNotStopRecord', ['Could not tell Netstation to stop recording.  Please check the IP and connection and try again.\n  Error:' error]);            %              throw(ME);            disp('ERROR stopping recoring in Netstation!');        end                [status error] = NetStation('Disconnect');        if status ==1 % there was an error!            status            error            ME = MException('NETSTATION:CouldNotStopRecord', ['Could not tell Netstation to stop recording.  Please check the IP and connection and try again.\n  Error:' error]);            %              throw(ME);            disp('ERROR disconnecting from Netstation!');                    end    end        % above.  Importantly, it closes the onscreen window if it's open.    disp('Caught error and closing experiment nicely....');    Screen('CloseAll');    Priority(0);    fclose('all');    psychrethrow(psychlasterror);endendfunction numericalanswer = getAnimalResponseMouseWait()[x,y,buttons] = GetMouse();while (~any(buttons))    [x,y,buttons] = GetMouse();endif buttons(1)    numericalanswer = 1;elseif buttons(3)    numericalanswer = 2;else    numericalanswer = 0;endendfunction numericalanswer = getAnimalResponseWait()numericalanswer = -1;while numericalanswer == -1    [secs, keyCode] = KbPressWait;    numericalanswer = translateKeyAnimalResponse(keyCode);endendfunction numericalanswer = getAnimalResponseMouse(waitTime)%Wait for a responsenumericalanswer = -1;mousePressed = 0;startWaiting=GetSecs;while GetSecs-startWaiting < waitTime && mousePressed == 0    %check to see if a button is pressed    [x,y,buttons] = GetMouse();    if (~buttons(1) && ~buttons(3))        continue;    else        if buttons(1)            numericalanswer = 1;        elseif buttons(3)            numericalanswer = 2;        else            numericalanswer = 0;        end        if numericalanswer ~= -1            %stop checking for a button press            mousePressed = 1;        end    end    endif numericalanswer == -1    numericalanswer =0;endendfunction numericalanswer = getAnimalResponse(waitTime)%Wait for a responsenumericalanswer = -1;keyPressed = 0;startWaiting=GetSecs;while GetSecs-startWaiting < waitTime && keyPressed == 0    %check to see if a button is pressed    [keyDown,secs,keyCode] = KbCheck;        %program accepts only RightArrow, LeftArrow and space as subject responses    if keyPressed == 0 && keyDown        numericalanswer = translateKeyAnimalResponse(keyCode);        if numericalanswer ~= -1            %stop checking for a button press            keyPressed = 1;        end    endendif numericalanswer == -1    numericalanswer =0;endendfunction numericalanswer = getRatingResponseWait()numericalanswer = -1;while numericalanswer == -1    [secs, keyCode] = KbPressWait;    numericalanswer = translateKeyRatingResponse(keyCode);endendfunction numericalanswer = getRatingResponse(waitTime)%Wait for a responsenumericalanswer = -1;keyPressed = 0;startWaiting=GetSecs;while GetSecs-startWaiting < waitTime && keyPressed == 0    %check to see if a button is pressed    [keyDown,secs,keyCode] = KbCheck;        %program accepts only RightArrow, LeftArrow and space as subject responses    if keyPressed == 0 && keyDown        numericalanswer = translateKeyRatingReponse(keyCode);        if numericalanswer ~= -1            %stop checking for a button press            keyPressed = 1;        end    endendendfunction [number] = translateKeyAnimalResponse(keyCode)keyName = KbName(keyCode);%         disp(['Pressed key ' keyName]);if strcmp(keyName,'LeftArrow') || strcmp(keyName,'KP_Left')    number = 1;elseif strcmp(keyName,'RightArrow') || strcmp(keyName,'KP_Right')    number = 2;else    number = -1;endreturn;% You pressed key 101 which is LeftArrow% You pressed key 103 which is RightArrow% You pressed key 84 which is KP_Left% You pressed key 86 which is KP_Rightendfunction [number] = translateKeyRatingResponse(keyCode)keyName = KbName(keyCode);%         disp(['Pressed key ' keyName]);if strcmp(keyName,'KP_End') || strcmp(keyName,'1')    number = 1;elseif strcmp(keyName,'KP_Down') || strcmp(keyName,'2')    number = 2;elseif strcmp(keyName,'KP_Next') || strcmp(keyName,'3')    number = 3;elseif strcmp(keyName,'KP_Left') || strcmp(keyName,'4')    number = 4;else    number = -1;endreturn;%             key 88 which is KP_End  = 1 key%             key 89 which is KP_Down = 2 key%             key 90 which is KP_Next = 3 key%             key 84 which is KP_Left = 4 key%             key 11 which is 1 key%             key 12 which is 2 key%             key 13 which is 3 key%             key 14 which is 4 keyendfunction [images] = loadimages(directory,filenames)total = size(filenames,1);for i=1:total    [value map] = imread([directory, filenames(i).name],'JPG');    images(i,:,:) = value(:,:);endendfunction [images] = loadimagesscale(directory,filenames,scaleval)total = size(filenames,1);if nargin < 3 || scaleval <= 0    dotheresize = 0;else    dotheresize = 1;endfor i=1:total    %         disp(['Loading image: ' directory filenames(i).name]);    [value map] = imread([directory, filenames(i).name],'BMP');    value = ind2gray(value,map);    %         size(value)    if (dotheresize)        value = imresize(value,scaleval);    end    images(i,:,:) = value(:,:);endendfunction drawAndCenterText(window,message,wait)if nargin < 3    wait = 1;end% Now horizontally and vertically centered:[nx, ny, bbox] = DrawFormattedText(window, message, 'center', 'center', 0);black = BlackIndex(window); % pixel value for blackScreen('FillRect',window,black,[0 0 32 32]);  % This is the stimulus marker block for the photodiodeScreen('Flip',window);%     KbWait;%     while KbCheck; end;if wait    KbPressWaitendendfunction writeExperimentParameters()if exist(filename,'file') ~= 2    % names    fwid = fopen(filename,'a+');    savecomment(fwid,['Data File Name: ',filename]);    savecomment(fwid,'face phase experiment');    savecomment(fwid,['image names: ',strcat(names{1:end})]);    savecommand(fwid,'usenewcliprandn = 1;');    savecommand(fwid,vars2str(name));    savecommand(fwid,vars2str(calfile));    savecommand(fwid,vars2str(calfolder));        % noise levels    savecommand(fwid,vars2str(noiselevel));    savecommand(fwid,vars2str(clockseed));        % stimulus parameters    savecommand(fwid,vars2str(numstim));    savecommand(fwid,vars2str(numstim));   