= 10;');    savecommand(fwid,'trial = 1; sequence = 2; response = 3; correct = 4; id = 5; coherence = 6; variance = 7; skewness = 8; kurtosis = 9; rt = 10; randnstate1 = 11; randnstate2 = 12;');    % date and time    savedatetime(fwid);    else    fwid = fopen(filename,'a+');    savedatetime(fwid);    eval(['load ',sourcename]);endend